import json


def decode_unicode(unicodes):
    """
    解码带转义字符的unicode字符串，结果为unicode字符串。
    :param unicodes: unicode字符串，如："\\u22\\u3e\\u25a0\\u3000"。
    :return:
    """

    item = list()
    items = list()
    for u in unicodes:
        u = str(u)
        if u != "\\":
            item.append(u)
        else:
            if item:
                items.append(item)
            item = list()
            item.append("\\")
    for i in range(len(items)):
        if len(items[i]) == 4:
            items[i].insert(2, "0")
            items[i].insert(2, "0")
        items[i] = "".join(items[i])
    content = "".join(items)
    return json.loads(f'"{content}"')


